import { useState } from "react"

const Addcourse = ({ onAdd }) => {

    const [code, setCode] = useState('')
    const [name, setName] = useState('')
    const [term, setTerm] = useState('')

    const onSubmit = (e) => {
        e.preventDefault()

        if (!code) {
            alert('Please add a course code')
            return
        }

        if (!name) {
            alert('Please add a course name')
            return
        }

        if (!term) {
            alert('Please add a course term')
            return
        }

        const course = {code, name, term}
        onAdd(course)

        setCode('')
        setName('')
        setTerm('')
     }

    return (
        <form className='add-form' onSubmit={onSubmit}>
            <div className='form-control'>
                <label>Course Code</label>
                <input type='text' placeholder='Add a course code' value={code} onChange={(e) => setCode(e.target.value)} />
            </div>

            <div className='form-control'>
                <label>Course Name</label>
                <input type='text' placeholder='Add a course name' value={name} onChange={(e) => setName(e.target.value)} />
            </div>

            <div className='form-control'>
                <label>Course Term</label>
                <input type='text' placeholder='Add a course term' value={term} onChange={(e) => setTerm(e.target.value)} />
            </div>

            <input type='submit' value='Save course' className='btn btn-block'/>
        </form>
    )
}

export default Addcourse
