import { useState } from "react";

const AddTask = ({ onAdd, courseList }) => {
  // TODO: Add more task attributes. Right now it's name, day and reminder
  // For example, you can add description, due day, importance, etc.

  // TODO: Change css if necessary; do this at the end

  const [name, setName] = useState("");
  const [day, setDay] = useState("");
  const [reminder, setReminder] = useState(false);
  const [course, setCourse] = useState("");

  const handleChange = (event) => {
    setCourse(event.target.value);
  };

  const onSubmit = (e) => {
    e.preventDefault();

    if (!name) {
      alert("Please add a task");
      return;
    }

    const task = { name, course, day, reminder };
    onAdd(task);

    //onAdd({ name, course, day, reminder });

    setName("");
    setDay("");
    setReminder(false);
  };
  return (
    <form className="add-form" onSubmit={onSubmit}>
      <div className="form-control">
        <label>Task</label>
        <input
          type="text"
          placeholder="Add Task"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
      </div>

      <div className="form-control">
        <label htmlFor="courses">Course</label>
        <select
          name="courses"
          id="courses"
          value={course}
          onChange={handleChange}
        >
          <option value=""></option>
          {courseList.map((course, key) => (
            <option value={course.code} key={key}>
              {course.code}: {course.name}
            </option>
          ))}
        </select>
      </div>

      <div className="form-control">
        <label>Deadline</label>
        <input
          type="date"
          placeholder="Add Deadline"
          value={day}
          onChange={(e) => setDay(e.target.value)}
        />
      </div>

      <div className="form-control form-control-check">
        <label>Set Reminder</label>
        <input
          type="checkbox"
          checked={reminder}
          value={reminder}
          onChange={(e) => setReminder(e.currentTarget.checked)}
        />
      </div>

      <input type="submit" value="Save Task" className="btn btn-block" />
    </form>
  );
};

export default AddTask;
