import { useState } from "react";
import { FaTimes, FaPen, FaSave, FaUndoAlt } from "react-icons/fa";

const Task = ({
  task,
  onDelete,
  editMode,
  setTaskToEdit,
  onEdit,
  courseList,
}) => {
  const [name, setName] = useState(task.text);
  const [course, setCourse] = useState(task.course);
  function handleChangeName(event) {
    setName(event.target.value);
  }
  function handleChangeCourse(event) {
    setCourse(event.target.value);
  }

  return (
    <div className={`task ${task.reminder && "reminder"}`}>
      <h3>
        {editMode ? (
          <input type="text" value={name} onChange={handleChangeName} />
        ) : (
          name
        )}{" "}
        {task.toggleMode}
        {!editMode && (
          <FaPen
            data-testid="edit task"
            onClick={() => setTaskToEdit(task.id)}
          />
        )}
        {editMode && (
          <FaSave
            data-testid="save task"
            onClick={() => {
              if (onEdit(name, course)) {
                setTaskToEdit(-1);
              }
            }}
          />
        )}
        {editMode && (
          <FaUndoAlt
            data-testid="edit task"
            onClick={() => {
              setTaskToEdit(-1);
              setName(task.text);
            }}
          />
        )}
        <FaTimes
          data-testid="delete task"
          style={{ color: "red", cursor: "pointer" }}
          onClick={() => onDelete(task.id)}
        />
      </h3>
      <p>
        {editMode ? (
          <select
            name="courses"
            id="courses"
            value={course}
            onChange={handleChangeCourse}
          >
            <option value=""></option>
            {courseList.map((course) => (
              <option value={course.code} key={course.id}>
                {course.code}: {course.name}
              </option>
            ))}
          </select>
        ) : (
          course
        )}
      </p>
      <p>{task.day}</p>
    </div>
  );
};

export default Task;
