import Task from "./Task";

const Tasks = ({
  tasks,
  onDelete,
  taskToEdit,
  setTaskToEdit,
  onEdit,
  courseList,
}) => {
  return (
    <>
      {tasks.map((task, index) => (
        <Task
          key={index}
          task={task}
          onDelete={onDelete}
          editMode={taskToEdit === task.id}
          setTaskToEdit={setTaskToEdit}
          onEdit={onEdit}
          courseList={courseList}
        />
      ))}
    </>
  );
};

export default Tasks;
